    <script>
        $('form#searchquestion').submit(function(event){
            event.preventDefault();
            var formData=$(this).serialize();
            $.ajax({
                url: 'http://localhost:8000/api/searchquestion',
                type: 'POST',
                data: formData,
                success: function(response) {
                    console.log(response);
                    window.location.href=`http://localhost:8000/questiondetail/${response}`
                },
                error: function(error) {
                    console.log(error);
                }
            });

        })
    </script>
